void BFS( Vertex V ){
    queue q;
    visited[V] = true;
    q.push(V);
    while(!q.empty()){
        V = q.front(); q.pop();
        for( V 的每个邻接点 W ){
            if( !visited[ W ]){
                visited[W] = true;
                q.push(W);
            }
        }
    }
}
