        pos = Hash(H->TableSize,Key);   // 初始散列表地址
        // 将新增结点插到最前面
        NewCell->Next = H->Heads[pos].Next;
        H->Heads[pos].Next = NewCell;
        return true;
    }else{
        return false;
    }
}

