void shell_sort( vector& arr , int n){
    for( int gap=n/2; gap>0 ;gap/=2){
        for( int i=gap ;i=0 && arr[j-gap] > sentinel ){
                    arr[j]= arr[j-gap];
                    j-=gap;
                }
                arr[j]= sentinel;
            }
        }
    }
